create table IF NOT EXISTS ALBUM (ID bigint generated by default as identity, NAME varchar(100) not null, COVER_IMG varchar(255), ARTIST_ID bigint not null, GENRE_ID bigint not null, primary key (ID));
create table IF NOT EXISTS ARTIST (ID bigint generated by default as identity, NAME varchar(40) not null, primary key (ID));
create table IF NOT EXISTS GENRE (ID bigint generated by default as identity, NAME varchar(100) not null, DESC varchar(450) not null, primary key (ID));
create table IF NOT EXISTS PLAYLIST (ID bigint generated by default as identity, NAME varchar(100) not null, DESC varchar(500), ARTWORK varchar(1000) primary key (ID));
create table IF NOT EXISTS TRACK (ID bigint generated by default as identity, NAME varchar(100) not null, DURATION int not null, LYRICS varchar(255), ALBUM_ID bigint not null, GENRE_ID bigint, PLAYLIST_ID bigint, primary key (ID));